sys_ui_action.script:
try { 

     var gd = new GlideDate(); 
     gd.setValue(current.getValue('date')); 
     var month = gd.getMonthNoTZ(); 
     var day = gd.getDayOfMonthNoTZ(); 

     var r = new sn_ws.RESTMessageV2('x_otd.Wikipedia', 'Get Events'); 
     r.setStringParameterNoEscape('day', day); 
     r.setStringParameterNoEscape('month', month); 

     var response = r.execute(); 
     var responseBody = response.getBody(); 
     var httpStatus = response.getStatusCode(); 
     gs.addInfoMessage('HTTP status=' + httpStatus); 

     // Parse the records 
     var respObj = JSON.parse(responseBody); 
     gs.addInfoMessage(respObj.events.length + ' events found'); 

     // Now save the records 
     for (var i = 0; i < respObj.events.length; i++) {
          var eGr = new GlideRecord('x_otd_event');
          event = respObj.events[i];
          eGr.year = event.year;
          eGr.text = event.text;
          eGr.page = current.sys_id;
          eGr.insert();
     }

     current.state = 'Retrieved'; 
     current.update(); 
     action.setRedirectURL(current);
} 
catch(ex) { 
     var message = ex.getMessage(); 
}
